import React, { useState, useRef, useEffect } from "react";
import "./Cursor.scss";
import { motion } from "framer-motion";

import gsap from "gsap";
import { useGSAP } from "@gsap/react";
gsap.registerPlugin(useGSAP);

const Cursor = () => {
  const [circleSize, setCircleSize] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (event) => {
      console.log({
        x: event.clientX,
        y: event.clientY
      });
      setCircleSize({ x: event.clientX, y: event.clientY });
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  const variants = {
    default: {
      x: circleSize.x,
      y: circleSize.y
    },
  };

  return (
    <motion.div
      variants={variants}
      animate="default"
      className="Cursor"
      transition={{
        x: {
          duration: 0.3,
          ease: "linear",
          repeat: 0,
          type: "spring",
          stiffness: 80,
        },
        y: {
          duration: 0.3,
          ease: "linear",
          repeat: 0,
          type: "spring",
          stiffness: 80,
        },
        default: {
          duration: 2.5,
          repeat: Infinity,
        },
      }}
    >
    </motion.div>
  );
};

export default Cursor;
